let movie1 = {
    name: "Harry Potter",
    img: "data:image/png;base64,"
}
let movie2 = {
    name: "Titanic",
    img: "data:image/jpeg;base64,"
}
let movie3 = {
    name: "SnowWhite",
    img: "data:image/jpeg;base64,"
}

let movies = [];

movies.push(movie1);
movies.push(movie2);
movies.push(movie3);

let movieContainer = document.getElementById("container");

movies.forEach((movie) => {
    movieContainer.innerHTML += `
 <div class="movie">
 <img src="${movie.img}" alt="movie picture" />
 <h2>${movie.name}</h2>
 </div>
`;

});

let randommovie = Math.floor(Math.random() * 3);
let TodaysMovie = movies[randommovie];

movieContainer.innerHTML += `
<div class="movie">
<h1>Todays Movie</h1>
<img src="${TodaysMovie.img}" alt="movie picture" />
<h2>${TodaysMovie.name}</h2>
</div>
`;